package top.ivan.jardrop.common.cache;

import lombok.AllArgsConstructor;
import lombok.Getter;
import lombok.NonNull;
import lombok.extern.slf4j.Slf4j;
import top.ivan.jardrop.common.challenge.AccessLimiter;
import top.ivan.jardrop.common.challenge.ChallengeTask;
import top.ivan.jardrop.common.util.IDUtils;

import java.nio.charset.StandardCharsets;
import java.util.concurrent.ConcurrentHashMap;
import java.util.function.Function;
import java.util.function.Supplier;

/**
 * @author Ivan
 * @description
 * @date 2021/3/26
 */
@Slf4j
public class LimitCache<T> {

    private static final CacheData<?> NONE = new CacheData<>(ChallengeTask.State.NONE, null);
    private final ConcurrentHashMap<String, InitObject<T>> supplierMap = new ConcurrentHashMap<>();

    private final AccessLimiter accessLimiter = new AccessLimiter();

    public String register(Function<String, T> supplier, int count, int expireMillions) {
        String key = IDUtils.getShortUuid();
        register(key, () -> supplier.apply(key), count, expireMillions);
        return key;
    }

    public void register(String key, Supplier<T> supplier, int count, int expireMillions) {
        if (count < 1) {
            count = Integer.MAX_VALUE;
        }
        if (expireMillions < 1) {
            expireMillions = Integer.MAX_VALUE;
        }

        InitObject<T> obj = new InitObject<>(supplier);
        supplierMap.put(key, obj);
        accessLimiter.register(key, expireMillions, count, task -> supplierMap.remove(key, obj));
    }

    public T getData(String key) throws CacheNotAccessableException {
        InitObject<T> supplier = supplierMap.get(key);

        switch (accessLimiter.challenge(key)) {
            case NONE:
                throw new CacheNotAccessableException("no resource for key '" + key + "'");
            case FAILED:
                throw new CacheNotAccessableException("resource for key '" + key + "' access limited");
            case TIMEOUT:
                throw new CacheNotAccessableException("resource for key '" + key + "' expired");
        }

        if (null == supplier) {
            throw new CacheNotAccessableException("resource for key '" + key + "' expired");
        }

        return supplier.get();
    }

    @SuppressWarnings("unchecked")
    public CacheData<T> getCacheData(String key) {
        if (null == key || !supplierMap.containsKey(key)) {
            return (CacheData<T>) NONE;
        }
        InitObject<T> supplier = supplierMap.get(key);
        ChallengeTask.State state = accessLimiter.challenge(key);
        return new CacheData<>(state, supplier);
    }

    public void remove(@NonNull String key) {
        supplierMap.remove(key);
        accessLimiter.remove(key);
    }

    @AllArgsConstructor
    public static class CacheData<V> {
        @Getter
        private ChallengeTask.State state;
        private InitObject<V> supplier;

        public boolean isTimeout() {
            return state == ChallengeTask.State.TIMEOUT;
        }

        public boolean isLimit() {
            return state == ChallengeTask.State.FAILED;
        }

        public boolean isExist() {
            return state != ChallengeTask.State.NONE;
        }

        public boolean isAccessible() {
            return state == ChallengeTask.State.SUCCESS;
        }

        public V getData() {
            if (!isAccessible()) {
                return null;
            }
            return peek();
        }

        public V peek() {
            return supplier == null ? null : supplier.get();
        }
    }

    public static void main(String[] args) {

//        String aaa= "\\u003Cp\\u003E\uE423乾皇朝，疆域亿\uE466\uE44D。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E北\uE45D，黑潭\uE434，七\uE402初九。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E统治周遭百岭\uE53C\uE45D\uE500\uE480千\uE527\uE53C久\uE4F3黑潭\uE434\uE41F\uE423\uE4D7族\uE53C\uE4DE‘姜氏\uE4D7族’，\uE4CD今\uE500\uE4CA\uE4A0\uE436\uE50F\uE4F4存亡\uE4F3\uE4FC刻。\uE4D7族土城\uE4F3练武\uE3F0，\uE41F五\uE3FD\uE4F3姜玄护\uE444\uE41F\uE551\uE3FD\uE4F3妹妹站\uE3E9\uE4C2群\uE44D，\uE513\uE444擂台\uE47D。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“姜氏\uE43A\uE3F9\uE480\uE409\uE4C2，敢\uE509\uE535\uE54C\uE4F4\uE405？”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“今\uE475\uE550\uE41E\uE487\uE55A姜氏亡族\uE53C\uE475！哈哈哈哈哈！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E擂台\uE47D，申屠\uE4D7族\uE43C\uE4EB强\uE4C4“申屠勇”甩\uE436甩沾血\uE4F3刀，狞\uE4D6\uE43F嚣，\uE3E9\uE48E脚\uE498，\uE4A5\uE4FE\uE436\uE4DE具姜氏\uE43C\uE4EB强\uE4C4\uE4F3尸\uE4C8。按\uE423乾皇朝律，\uE4D7族冲突\uE4F0展\uE4A0极致，\uE548\uE52A\uE43A\uE414\uE49A\uE54B灭族，需\uE41B契约，\uE3E9\uE3EA城监察\uE4D2\uE4F3\uE4E5证\uE531，\uE47D擂台\uE4C0\uE50F\uE4F4\uE405。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E允\uE501\uE404\uE405\uE4C4仅限\uE4D7族\uE4E9\uE527\uE409\uE42A。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“二\uE524\uE535\uE405\uE4F4\uE436。”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE4AA\uE527\uE436，连二\uE524\uE535\uE548输\uE436。”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E练武\uE3F0围观\uE4F3\uE48D\uE4C2\uE55A\uE4DE片悲恸。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4D7族\uE50F\uE4F4\uE405\uE41E决\uE4AB\uE4DE\uE449\uE4D7族\uE426运\uE4F3终极\uE53C\uE405，输\uE3EC\uE414输掉\uE4DE切，\uE49B\uE480财产归\uE49A\uE54B\uE49B\uE480，\uE49B\uE480\uE4CE童\uE414\uE4B5改\uE471\uE49A\uE54B\uE4D7族\uE4F3姓氏，\uE48D\uE4C2则\uE427\uE40E归\uE4ED“贱籍”，\uE4E9\uE471奴隶玩\uE4D3，\uE43A\uE438随\uE49F糟透贩卖。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“哥，哥\uE487\uE477，青红姐拿\uE444刀，\uE547\uE50B干\uE411\uE50D？”妹妹姜瑶怯\uE50F\uE50F躲\uE3E9姜玄\uE489\uE428，\uE3F3探\uE404\uE449\uE4E4，紧\uE420\uE508\uE488\uE48B。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E左\uE498\uE4C2群\uE44D，\uE53E挑绝\uE499肤\uE473似雪\uE4F3红衣\uE44B\uE48D，\uE547右\uE4F5反握\uE444匕首背\uE3E9\uE489\uE428，凝\uE513擂台\uE47D，\uE522\uE51C冰冷。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E姜青红\uE490姜玄\uE423\uE551\uE3FD。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE41E姜玄暗恋\uE4FE\uE527\uE4F3\uE49A\uE401。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE547准备\uE4E7杀。”姜玄\uE528皮跳\uE436跳低沉\uE49E\uE459，垂\uE444\uE4F3\uE4F5\uE531\uE49F识捏紧\uE436拳\uE4E4。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE52A仅仅姜青红准备\uE4E7杀。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE3E9\uE3F0\uE552百\uE4EE\uE48D\uE4C2\uE554，\uE500\uE4CA\uE4C9\uE460\uE4E7杀准备\uE4F3\uE52A\uE3E9\uE44B\uE552。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E越漂亮\uE4F3决\uE4F9越\uE423。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4A7\uE471\uE3F2输\uE436！\\u003C\\u002Fp\\u003E\\u003Cp\\u003E姜氏\uE509申屠氏\uE4F3“\uE4D7族\uE50F\uE4F4\uE405”，\uE500\uE4CA\uE4C0\uE436足足\uE4AA\uE527，按规则，\uE4D7族\uE50F\uE4F4\uE405\uE54E\uE449\uE402\uE3F3\uE4C0\uE4AA\uE4EB，\uE54E\uE402\uE4F3初七\uE4A0初九，\uE54E\uE4EB\uE4C0\uE41F\uE3F0，\uE3EA城监察\uE4D2\uE41A\uE3E9\uE51E\uE4AA\uE4EB\uE4FC\uE526\uE44D，\uE4FA\uE4A0\uE4D7族领\uE45D\uE418裁判。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4AA\uE527\uE50F\uE4F4\uE405，姜氏\uE405\uE4F4\uE436\uE552百\uE4EE嫡系勇\uE442。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4CD今\uE4F3姜氏\uE4D7族，嫡系\uE409\uE4C2\uE474乎\uE4F4绝。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E今\uE4EB\uE4FA练武\uE3F0\uE47D观\uE405\uE4F3姜氏嫡系族\uE4C2\uE480近七百\uE4C2，\uE431\uE4EF\uE554\uE480超\uE4BC六百\uE4C2\uE40E\uE41E\uE48D\uE4C2，\uE47D\uE503七旬\uE535妪，\uE531\uE503\uE474\uE3FD\uE48D童，\uE438二\uE4AA\uE41F\uE3FD\uE4F3貌\uE499\uE48D\uE4C2居\uE4FE，\uE409\uE42A\uE52A\uE41E\uE46A\uE480，\uE478基\uE4BA\uE40E\uE41E\uE4CE童\uE4B0\uE41F\uE474\uE3FD\uE4F3半\uE423\uE4CE\uE54C。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE487\uE55A姜氏\uE415\uE4F3\uE46A\uE480\uE409\uE4C2\uE436嘛？”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“哈哈哈哈！\uE487\uE55A姜氏\uE46A\uE480\uE517！\uE4B4\uE44A\uE487\uE55A\uE505\uE4FE\uE409\uE4C2\uE40E\uE3E9\uE51E\uE474\uE527跑掉\uE436，根\uE4BA\uE52A敢跟\uE521\uE55A申屠氏\uE4C0\uE531\uE4C5！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE521\uE511\uE48B\uE4DE遍！今\uE475\uE4D5\uE428\uE4DE\uE405，谁敢\uE4FA\uE405？”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E申屠勇踩\uE444二\uE524\uE535\uE4F3尸\uE4C8，咧嘴狞\uE4D6。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4DE\uE41C\uE48D\uE4CE\uE40E\uE42C申屠勇野蛮\uE4F3姿态吓哭\uE436，\uE532\uE4A5捂\uE444嘴\uE52A敢哭\uE404\uE488\uE4FA。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE55B\uE439\uE4D7族\uE4DE旦输掉\uE4D7族\uE50F\uE4F4\uE405，\uE531\uE3F0\uE40E\uE414极\uE471悲惨。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E尤\uE4EF\uE41E\uE4D7族\uE44D\uE4F3\uE48D\uE4C2。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4D7族\uE50F\uE4F4\uE405决\uE404\uE4D5终胜负\uE428，胜\uE486\uE4D7族\uE414举\uE4DD“\uE4AA\uE475狂欢”，\uE3E9\uE51E\uE4AA\uE4EB\uE4FC\uE526\uE44D，输\uE3EC\uE4D7族\uE4F3\uE49B\uE480\uE48D\uE42A\uE40E\uE414遭\uE4A0凌辱，\uE53C\uE428\uE49D\uE531\uE4FA\uE4F3\uE423\uE4D7\uE50E\uE414\uE4E9\uE471胜\uE486\uE4D7族\uE4F3族妓，\uE3F3\uE480极\uE44B\uE552“运\uE422\uE460”\uE4F3，\uE43A\uE4EC\uE41A\uE42C胜\uE486\uE4D7族\uE4F3强\uE4C4收\uE471禁脔，\uE4B0\uE41E\uE42C“飞雪城”\uE4F3\uE535爷买\uE436\uE4C5。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE487\uE4C3\uE41E姜青红吧？”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E申屠勇环伺擂台周围，\uE541\uE4E2突\uE3EE\uE4AB\uE3E9\uE436姜青红\uE4F3脸\uE47D，舔\uE436舔嘴角，“\uE52A愧\uE41E姜氏\uE4D7族\uE479\uE4DE\uE499\uE4C2，\uE499\uE4F3\uE4DE\uE528\uE4C3\uE457\uE4B7\uE404，\uE4B4\uE44A呼延氏\uE4F3\uE44B\uE3EA，愿\uE49F\uE438五座\uE434\uE4E4\uE47A五\uE449\uE4D7落\uE471聘礼，娶\uE487\uE4BC\uE4C5，\uE43A\uE487\uE40E\uE46A答\uE484，\uE44A\uE411\uE50D\uE4F4\uE548\uE3F2\uE4F4\uE3E9姜氏。”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE487\uE51E\uE517\uE499\uE4C2，\uE4F4\uE436\uE4FE\uE43A惜啊，\uE4DA\uE41A\uE3FC\uE4BF叔叔\uE460\uE460疼疼\uE487，哈哈哈哈！”申屠勇戏谑狂\uE4D6。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“哈哈哈哈！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE49A，咱\uE55A\uE4DE\uE545\uE460\uE460疼疼\uE547！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E擂台南\uE498，\uE552\uE41F\uE4EE申屠\uE4D7族勇\uE442跟\uE444\uE4DE\uE545\uE423\uE4D6。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE487休\uE50B！”姜青红\uE499\uE541瞪圆\uE436咬牙\uE459，\uE522态冰冷\uE554透\uE444决绝，背\uE428握\uE444刀\uE4F3\uE4F5，\uE3E9止\uE52A\uE47F\uE4F3颤抖。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“哥，\uE487\uE52A\uE41E喜欢青红姐吗？”姜玄\uE489\uE428\uE4F3姜瑶\uE4A5\uE508\uE488\uE459，“\uE487跟娘\uE44A，\uE518青红姐\uE4DE\uE545\uE4CC\uE460\uE52A\uE460？”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E姜氏\uE4D7族盛产俊\uE409靓\uE48D，姜青红则\uE41E\uE499\uE4C2\uE554\uE4F3\uE499\uE4C2，\uE4EE\uE488\uE3E9\uE556，姜玄\uE52A\uE4BC\uE41E暗恋姜青红\uE4F3\uE4D7族\uE44B\uE527\uE53C\uE4DE，\uE431\uE48E\uE4A4\uE459，“青红姐”\uE41E\uE477\uE444\uE4E7\uE534\uE524\uE423\uE4F3，\uE4DE\uE40A\uE465\uE4E7\uE534\uE418弟弟\uE477待，\uE3E9\uE547\uE528\uE44D\uE4E7\uE534\uE52A\uE4BC\uE41E\uE4DE\uE449\uE508\uE409\uE4CE。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“娘\uE52A\uE41A\uE467\uE49F\uE4F3。”姜玄低\uE488\uE459。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE43A\uE41E……”姜瑶\uE528睛红\uE436。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“爹\uE52A\uE3E9\uE436。”姜玄低\uE488连\uE459，“娘拼\uE436\uE426\uE4F3\uE3F2保护\uE521\uE55A，\uE52A\uE41A节\uE556\uE50F枝。”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E姜玄\uE491\uE52B，副族\uE524姜寒峰，\uE41E姜氏\uE4D7族\uE479\uE4DE强\uE4C4，\uE548\uE41E族\uE44D唯\uE4DE领悟“\uE4EB\uE4C2\uE53C势”\uE4F3顶尖存\uE3E9。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4AA\uE527\uE4FE\uE53D，姜寒峰\uE40B踪\uE4ED黑潭\uE434深\uE4EA\uE41F\uE54B绝\uE45D。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E姜寒峰\uE40B踪\uE428\uE4F3\uE479\uE4AA\uE449\uE402，申屠\uE4D7族\uE457\uE4AB\uE4EF\uE4F4亡，\uE428挑\uE545冲突，\uE4F0\uE4E1\uE4D7族\uE50F\uE4F4\uE405，申屠氏\uE3F2\uE490姜氏强\uE423\uE501\uE4FE，\uE40E\uE4A4\uE459姜氏\uE4D7族早晚\uE41A输，\uE51E\uE4AA\uE527\uE505\uE4FE\uE4C2\uE40E逃离\uE436姜氏\uE4D7族，\uE478\uE548\uE4B7\uE41E\uE480\uE4BA\uE483\uE4F3\uE4C2\uE51D\uE4EC逃掉，\uE556\uE506妖兽横\uE4DD，\uE46A\uE480足够\uE4F3\uE51B\uE4FD，\uE404\uE4C5\uE548\uE41E送\uE4F4。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E姜玄\uE4F3\uE4F2\uE52B“卫\uE473筠”\uE4DE\uE40A\uE3E9\uE4DA丈\uE4B1\uE49E\uE4FA。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4CD今\uE548\uE500\uE4CA决\uE4AB\uE518\uE3FC\uE54C\uE48D\uE3FC逃\uE436。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E虽\uE3EE\uE51E\uE41A\uE4A7违反\uE4D7族\uE50F\uE4F4\uE405契约，\uE431\uE42C皇朝\uE42B缉，\uE478\uE4F1\uE490\uE427\uE3EC\uE40A\uE430\uE4E9\uE471奴隶\uE3F2\uE460。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“五\uE524\uE535\uE53A？五\uE524\uE535\uE4C5哪\uE3FC\uE436？”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“五\uE524\uE535半\uE449\uE402\uE53D\uE4A5\uE480突破，申屠勇今\uE475连\uE405九\uE3F0，刚刚胜\uE436\uE548\uE41E惨胜，跟二\uE524\uE535交\uE4F5\uE4FC\uE4C3\uE500吐血，\uE3FB\uE3E9装\uE47C\uE46A\uE483，\uE51B际\uE47D\uE4A6伤\uE505\uE533，肯\uE4AB\uE52A\uE41E五\uE524\uE535\uE49A\uE4F5，\uE3F3\uE3F2五\uE524\uE535\uE404\uE4F5，\uE51E\uE449\uE402\uE50F\uE4F4\uE405\uE521\uE55A\uE4EC撑\uE4BC\uE4C5，拖\uE4A0\uE531\uE449\uE402，\uE44A\uE52A\uE4AB寒峰叔\uE4C3\uE49E\uE4FA\uE436，\uE4CD\uE4E0\uE48E\uE3F9\uE49D\uE444\uE4F3\uE43B……”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“五\uE524\uE535，\uE4DE\uE4AB\uE4EC赢！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE3E9姜氏\uE4D7族\uE48D\uE4C2\uE55A\uE4F3\uE553盼\uE554，\uE527\uE4BC半百\uE532\uE4E4\uE4F0乌黑\uE529\uE490壮硕\uE4F3五\uE524\uE535姜景盛姗姗\uE4FA迟。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE467\uE4FC，\uE4DE\uE459穿\uE444\uE524袍戴\uE444兜帽\uE4F3\uE522秘\uE489影挤\uE4F6\uE4C2群，\uE4CC\uE4A0\uE436姜玄\uE509姜瑶兄妹\uE489旁。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“娘。”姜玄\uE509姜瑶\uE467\uE4FC\uE4FF\uE510。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E卫\uE473筠摘掉\uE524袍兜帽，露\uE404端庄秀\uE499\uE4F3容颜，\uE547\uE480\uE444\uE53B显区\uE3F8\uE4ED\uE4D7族\uE48D\uE4C2\uE4F3贵\uE422，肌肤晶莹\uE4CD\uE44B\uE48D，\uE53B\uE53B\uE500\uE4CA\uE527近\uE551旬，\uE3FD\uE402\uE532\uE46A\uE480\uE3E9\uE547脸\uE47D留\uE531\uE4FE\uE44B痕迹，仿佛\uE3F9\uE41E二\uE41F\uE474\uE3FD\uE4DE般。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E卫\uE3EC乃\uE41E\uE3EA城飞雪城\uE551\uE423\uE3EC族\uE53C\uE4DE。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E卫\uE473筠\uE4C6\uE4BA\uE41E卫\uE3EC\uE4F3\uE508姐，\uE418\uE527\uE531嫁\uE4FA\uE4D7族，嫁\uE4D1姜寒峰，\uE548\uE41E轰\uE4E1\uE4DE\uE4FC\uE4F3\uE483，卫\uE473筠\uE4A7\uE400\uE3F9\uE42C卫\uE3EC除\uE4EE。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“娘，\uE4EC\uE518青红姐\uE4DE\uE545\uE4CC吗？”姜瑶\uE47E\uE436\uE47E\uE4F2\uE52B\uE4F3衣角。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E卫\uE473筠\uE477\uE4B2\uE48D\uE3FC，摸\uE436摸\uE48D\uE3FC\uE4F3\uE4E4\uE4F0，\uE46A\uE480\uE44A\uE43B。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“娘，您\uE43A\uE41E\uE43C\uE4EB强\uE4C4，\uE415\uE4F3\uE52A\uE4DD吗？”姜瑶\uE477\uE404\uE436\uE4F2\uE52B\uE4F3态\uE45E，泪\uE528汪汪。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“娘\uE43A\uE438\uE518\uE47D青红，\uE417娘\uE41E\uE52A\uE41E\uE3F9\uE3F2\uE518\uE47D\uE487幼微姐？”卫\uE473筠\uE44A\uE444\uE541\uE4E2扫视\uE4B2周围，“\uE3F9\uE480\uE487凤兰姐、盼雪姐、千荷姐、巧莲姐……\uE3F9\uE480\uE547\uE55A，若香、芝芸、念晴、映莲……\uE51E\uE50D\uE4FE\uE4C2，该抛\uE531谁，\uE4A5该\uE518\uE4CC谁？”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E卫\uE473筠\uE4DE\uE498\uE44A，\uE4DE\uE498\uE477\uE4B2\uE4C2群\uE44D\uE417\uE4DE\uE459\uE459\uE499丽\uE4F3\uE489影。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE547\uE528\uE554闪\uE4BC\uE4F9痛\uE53C\uE44C。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE51E\uE41C\uE499丽\uE4F3\uE4D7族\uE44B\uE48D，\uE40E\uE41E\uE547\uE477\uE444\uE524\uE423\uE4F3，\uE547刚嫁\uE4BC\uE4FA\uE4FC，\uE51E\uE41C\uE48D\uE4CE\uE40E\uE51D\uE474\uE3FD，\uE3FB\uE3E9\uE40E\uE41E\uE423姑娘\uE436，卫\uE473筠\uE4A4\uE459，\uE4DA待\uE547\uE55A\uE4F3\uE426运\uE41E\uE439\uE4DA残酷。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE43A\uE49A\uE547\uE431言，\uE3FC\uE48D\uE51D\uE41E\uE4D5\uE533\uE3F2\uE4F3。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4C3算\uE547\uE41E\uE43C\uE4EB强\uE4C4，\uE518\uE4DE\uE3FC\uE4DE\uE48D绕\uE4FF申屠\uE4D7族\uE4F3封锁逃\uE4CC，\uE500\uE4CA\uE41E极限，\uE52A\uE43A\uE4EC\uE4FE\uE518\uE55B\uE439\uE4DE\uE4C2。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E姜玄随\uE444\uE4F2\uE52B\uE4F3诉\uE44A，\uE4DE\uE467扫视。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE541\uE4E2\uE554仿佛\uE480\uE411\uE50D\uE3E9跳\uE4E1。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE51E\uE41C姐姐，\uE40F\uE475\uE44D\uE40E\uE49A\uE4E7\uE534\uE505\uE460。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E盼雪姐\uE4C9\uE4F3馍馍，\uE460\uE46D极\uE436。千荷姐\uE42A\uE523泼辣\uE556\uE4B2，\uE424\uE4FF\uE4DE\uE41C\uE518荤\uE4F3玩\uE4D6。若香姐、芝芸姐\uE4F5\uE505巧，娘\uE41E城\uE44D\uE423族嫁\uE4BC\uE4FA\uE4F3，修\uE4DD\uE4EB赋非\uE437\uE53E，\uE478\uE52A\uE41A\uE4C9\uE48D红，\uE4E7\uE534\uE509妹妹\uE51E\uE474\uE527穿\uE4F3衣服，\uE40E\uE41E\uE547\uE55A\uE4C9\uE4F3。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E映莲姐管\uE54F\uE444\uE4D7族\uE4F3药园，\uE4E7\uE534\uE54E\uE514练功\uE4DB伤，\uE40E找映莲姐\uE47D药，\uE547\uE548\uE4DE\uE40A帮忙瞒\uE444\uE4E7\uE534练功\uE4DB伤\uE4F3\uE483，\uE46A\uE4BF娘\uE4A4\uE459。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E姜玄\uE4DE\uE449\uE4DE\uE449\uE4F3\uE477\uE4BC\uE4C5。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE51E\uE41C族\uE44D\uE4F3姐姐、妹妹、姑姑。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE54E\uE4DE\uE449\uE40E\uE4EC勾\uE545\uE48E\uE4F3\uE4DE段\uE49E忆。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE49A\uE4ED逃\uE4CC\uE4F3\uE483，\uE4EF\uE51B姜玄\uE4DE\uE40A\uE480\uE4E7\uE534\uE4F3\uE50B\uE481。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE48E\uE52A\uE50B\uE511隐藏\uE51B\uE4FD\uE436。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E哪怕\uE51E\uE41A违背\uE4E7\uE534\uE509奇石\uE53D辈\uE4F3约\uE4AB。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“娘。”姜玄突\uE3EE\uE4FF\uE510，“您\uE44A，\uE4CD\uE4E0爹\uE3F9\uE3E9，\uE48E\uE41E希\uE513\uE521\uE46E\uE4DE\uE449懦\uE4B1\uE4DE\uE557逃\uE4CC，\uE3F9\uE41E\uE46E\uE449\uE409\uE4C2\uE4DE\uE557……”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE487\uE52A\uE41E懦\uE4B1！”卫\uE473筠猛\uE4F3\uE49E\uE4E4，\uE4C0断\uE436\uE3FC\uE54C\uE4F3\uE43B，“\uE487\uE3F9\uE41E\uE449\uE4CE\uE54C！\uE51E\uE52A\uE41E\uE487\uE4F3责\uE55B！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE547\uE52A\uE50B\uE3FC\uE54C冲\uE4E1送\uE4F4，\uE3FC\uE54C\uE4EB赋\uE40F庸，\uE4CD今\uE52A\uE4BC“\uE42B脉境”，\uE55B\uE439\uE4DE\uE449\uE43C\uE4EB强\uE4C4\uE40E\uE4EC\uE440易杀\uE436\uE48E。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE509\uE400\uE467\uE4FC。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE500\uE4CA登\uE47D擂台\uE4F3五\uE524\uE535姜景盛，\uE4A2二\uE524\uE535尸\uE4C8右\uE4F5\uE47D，摘\uE531\uE436\uE4DE枚古朴黑玉戒，\uE51E枚戒指\uE3E9姜氏\uE4D7族传承千\uE527，\uE41E族\uE524\uE489份\uE4F3\uE401征，\uE535族\uE524\uE425\uE527\uE53D\uE405\uE4F4\uE428，\uE4DE\uE40A\uE41E二\uE524\uE535\uE50C\uE54F族\uE524\uE53C\uE485，\uE4CD今\uE51E枚戒指，\uE4E7\uE3EE\uE3F2传\uE4A0姜氏\uE4D7族\uE4D5\uE428\uE4DE\uE449\uE409\uE4C2，五\uE524\uE535姜景盛\uE4F5\uE47D。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E五\uE524\uE535\uE414戒指郑\uE533戴\uE47D，\uE4DE脸冷\uE3EE\uE4F3\uE477\uE4B2申屠勇。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“五\uE524\uE535，杀\uE436\uE48E！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“五\uE524\uE535！\uE471二\uE524\uE535\uE456仇！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE48D\uE4C2\uE55A\uE471五\uE524\uE535姜景盛嘶喊\uE444，\uE547\uE55A\uE548\uE480血\uE42A，\uE505\uE4FE\uE4C2\uE4BA\uE4C3\uE41E\uE4D7族\uE4F3\uE405\uE442，\uE3F3\uE41E\uE4A7\uE471\uE423乾皇朝律\uE481，\uE547\uE55A\uE51D\uE46A\uE480资\uE523参\uE47A\uE50F\uE4F4\uE405。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4D7族\uE50F\uE4F4\uE405\uE4F3规则，\uE41E‘乾皇’\uE52B\uE4E7\uE4AB\uE531\uE4F3，\uE500\uE4CA\uE3E9\uE51E片\uE423\uE45D\uE47D流传\uE436\uE466\uE527\uE53C久，乾皇\uE47C\uE471亿\uE466\uE44D疆域\uE4D5古\uE535\uE4F3存\uE3E9\uE53C\uE4DE，\uE41E超越\uE522魔\uE4F3存\uE3E9，\uE46A\uE480\uE4C2\uE4EC改\uE445\uE48E\uE4AB\uE531\uE4F3规则。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“姜景盛，\uE4C3\uE487\uE548算\uE449\uE409\uE4C2？”申屠勇朝\uE444姜景盛咧嘴嗤\uE4D6，\uE53B\uE53B\uE51B\uE4FD\uE52A\uE4CD姜景盛，\uE477姜景盛\uE4F3\uE528\uE522\uE532\uE4C7\uE41E\uE52A屑。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE41E\uE41E\uE41E，\uE521\uE52A\uE41E\uE409\uE4C2。”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4BF\uE49B\uE480\uE4C2震惊\uE4F3\uE4DE幕\uE4F0\uE50F\uE436，姜景盛脸\uE47D\uE4F3冷\uE3EE瞬\uE526消\uE40B，取\uE431\uE50C\uE53C\uE4F3\uE41E\uE4DE脸谄媚堆\uE4D6，“勇\uE423\uE4C2\uE44A\uE4F3\uE49A！\uE521姜景盛跟您\uE490，\uE52A算\uE41E\uE409\uE4C2，\uE521\uE51E\uE4C3\uE50C\uE3EF姜氏\uE4D7族\uE427\uE4C8族\uE4C2，归顺\uE487\uE55A申屠\uE4D7族！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE44A\uE444，姜景盛转\uE489\uE477\uE4B2北\uE54B\uE4F3观\uE405台。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E观\uE405台修建\uE4F3极\uE471精\uE499，\uE47D\uE458\uE3F3\uE480\uE4DE\uE465椅\uE54C，\uE4DE\uE4EE\uE527约\uE551旬\uE4F3黑甲武\uE414\uE423\uE48C\uE49C刀\uE4F3坐\uE3E9\uE47D\uE458，\uE4DE脸漠\uE3EE\uE4F3\uE477\uE444擂台\uE47D\uE4F3\uE51C况。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“监察\uE4D2\uE423\uE4C2，今\uE475\uE479\uE41F\uE3F0\uE50F\uE4F4\uE405，\uE521\uE52A\uE4C0\uE436！\uE521\uE457输！”姜景盛极\uE471恭敬\uE459。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E监察\uE4D2\uE41E皇朝管\uE54F\uE4D7族\uE4D7落\uE4F3\uE3EB殊官差，\uE40E\uE41E\uE504超越\uE43C\uE4EB境\uE4F3恐怖存\uE3E9担\uE55B，\uE4EF\uE4DE\uE4C2\uE51B\uE4FD，\uE4C3\uE4EC随\uE550覆灭\uE4DE\uE449\uE4D7族。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“嗯。”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E监察\uE4D2雷鸿\uE49E\uE436\uE4DE\uE449淡漠\uE4F3鼻\uE537。\uE51E\uE517\uE52D\uE4E0\uE48E\uE4DE\uE4A8\uE40E\uE52A\uE49F\uE556，姜氏\uE4D7族\uE4BA\uE4C3\uE46A\uE480赢\uE4F3\uE43A\uE4EC，\uE47C\uE471裁决\uE4BC\uE505\uE4FE\uE449\uE4D7族\uE53C\uE526\uE50F\uE4F4\uE405\uE4F3监察\uE4D2，雷鸿\uE49A\uE4ED\uE51E\uE517背叛，\uE548\uE41E\uE4E5怪\uE52A怪。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E练武\uE3F0霎\uE4FC\uE526静\uE4F3落针\uE43A闻。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE49B\uE480姜氏族\uE4C2\uE40E\uE52A\uE43A置\uE530\uE4F3\uE477\uE4B2姜景盛。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE48E竟\uE3EE\uE457输\uE436！\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE48E——\uE42C收买\uE436！\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“哈哈哈哈哈，\uE521\uE44A\uE411\uE50D\uE4FA\uE444！\uE487\uE55A姜氏\uE40E\uE46A\uE480\uE517！哈哈哈哈哈！”申屠勇\uE4E5姜景盛按照约\uE4AB\uE457输\uE436，\uE4F9\uE548彻底\uE454\uE531，狂\uE4D6\uE52A止。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE52A\uE460！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4C2群\uE44D卫\uE473筠脸\uE44C巨\uE445。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E姜景盛背叛\uE4D7族\uE40A\uE430\uE457输，\uE479\uE41F\uE3F0\uE50F\uE4F4\uE405\uE529\uE4C2\uE404\uE405，\uE51E\uE41A导致姜氏\uE4D7族\uE41A\uE42C\uE40A\uE430判\uE4AB\uE471输！今\uE475\uE4C3\uE414\uE42C申屠\uE4D7族吞\uE4B9！\uE427族\uE471奴！\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE51E\uE4C0乱\uE436卫\uE473筠\uE518\uE3FC\uE48D逃\uE4CC\uE4F3计划。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4C6\uE4BA\uE4EC拖\uE4DE\uE449\uE402\uE4F3。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E卫\uE473筠\uE4F3\uE4C6计划\uE41E半\uE449\uE402\uE428找\uE461\uE41A\uE518\uE3FC\uE48D\uE404逃。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE3FB\uE3E9\uE46A\uE51E\uE449\uE461\uE41A\uE436。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE4CC，\uE521\uE55A\uE3FB\uE3E9\uE4C3\uE4CC！\uE447！”卫\uE473筠压低\uE488\uE537\uE49A\uE3FC\uE54C\uE48D\uE3FC急\uE459。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“卫\uE473筠！\uE487\uE4CC\uE4B7\uE436吗？”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E姜景盛突\uE3EE转\uE489，\uE40A勾勾\uE4F3\uE477\uE4B2\uE4C2群\uE44D\uE4F3卫\uE473筠，“\uE521早\uE4C3盯\uE444\uE487\uE53A！”\uE48E\uE4F3背叛\uE43A谓极\uE471彻底，\uE44A\uE444\uE48E\uE4A5\uE477\uE4B2申屠勇，堆\uE4D6\uE459：“勇\uE423\uE4C2！\uE417\uE48D\uE4C2\uE550\uE41E姜寒峰\uE4F3妻\uE54C，\uE404\uE489城\uE44D\uE423族卫\uE3EC，\uE52A\uE4BC\uE547\uE500\uE4CA\uE42C卫\uE3EC除\uE4EE\uE436，您尽管\uE454\uE4F9享\uE54A！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“城\uE44D\uE423族\uE4F3\uE508姐？”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E申屠勇\uE54A充\uE4C7冒犯\uE4F3\uE528\uE522\uE4C0量卫\uE473筠，\uE4C7\uE528垂涎\uE53C\uE44C，咧嘴\uE459，“\uE4B4\uE44A\uE487\uE3F9\uE41E\uE43C\uE4EB境，城\uE44D\uE423族\uE508姐\uE4F3滋味，\uE535\uE54C\uE43A\uE41E\uE3F9\uE46A尝\uE4BC，\uE4B5\uE3F8\uE44A\uE3F9\uE41E\uE4DE\uE449罕\uE4E5\uE4F3\uE43C\uE4EB\uE48D修\uE442\uE436！哈哈哈哈哈！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E卫\uE473筠咬\uE444牙脸\uE44C煞\uE473，\uE4C7\uE528怒\uE49F。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“欸？旁\uE498\uE41E\uE487\uE3FC\uE54C\uE48D\uE3FC吧，\uE524\uE4B7\uE3F9\uE415\uE46E。”申屠勇\uE4A5\uE541\uE4E2\uE4DE扫，\uE528睛\uE4B5亮，“尤\uE4EF\uE41E\uE487\uE51E\uE48D\uE3FC，跟\uE487简\uE40A\uE41E\uE4DE\uE449模\uE54C刻\uE404\uE4FA\uE4F3，\uE4DE\uE477\uE4C3\uE41E\uE499\uE4C2胚\uE54C，啧啧啧，\uE535\uE54C\uE460久\uE46A尝\uE4BC\uE487\uE55A\uE51E\uE517……”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE415\uE41E该\uE4F4！”\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4DE\uE488惊喝\uE4C0断\uE436申屠勇\uE4F3污言秽语。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE4DE\uE459\uE489影凶猛\uE4A2\uE4C2群\uE44D冲\uE404，\uE4DE踏步飞\uE489\uE552丈\uE40A扑\uE4A0擂台\uE47D，\uE422势极\uE471惊\uE4C2，凶悍\uE529\uE490，突\uE3EE\uE51C况吓\uE4B7申屠勇\uE509姜景盛\uE40E\uE531\uE49F识\uE4F3\uE428退\uE436\uE474步，\uE43A\uE418\uE425\uE4C2\uE477清突\uE3EE冲\uE47D\uE4FA\uE4F3\uE4C2\uE428，\uE4A5\uE40E\uE4D6\uE436。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E\uE52A\uE4BC\uE41E\uE4DE\uE449\uE4E5\uE4F2\uE52B妹妹\uE42C言语侮辱，\uE431\uE529\uE4EC狂怒\uE4F3半\uE423\uE4CE\uE54C罢\uE436。\\u003C\\u002Fp\\u003E\\u003Cp\\u003E“\uE41E姜寒峰\uE417\uE449废\uE4D3\uE3FC\uE54C。”\\u003C\\u002Fp\\u003E\"";
        String sdf = "\uE3EE\uE431\uE418\uE48E眨\uE436\uE531\uE528睛，\uE45D\uE47D\uE4F3血\uE44C\uE538符瞬\uE526消\uE40B，仿佛\uE4A2未存\uE3E9\uE4BC\uE4DE般。";
    }
}
